#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\danie\Documents\.tampermonkey-scripts\roll20\chrome-extension\node_modules\which\bin\node_modules;C:\Users\danie\Documents\.tampermonkey-scripts\roll20\chrome-extension\node_modules\which\node_modules;C:\Users\danie\Documents\.tampermonkey-scripts\roll20\chrome-extension\node_modules;C:\Users\danie\Documents\.tampermonkey-scripts\roll20\node_modules;C:\Users\danie\Documents\.tampermonkey-scripts\node_modules;C:\Users\danie\Documents\node_modules;C:\Users\danie\node_modules;C:\Users\node_modules;C:\node_modules;C:\Users\danie\Documents\.tampermonkey-scripts\roll20\chrome-extension\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/proc/cygdrive/c/Users/danie/Documents/.tampermonkey-scripts/roll20/chrome-extension/node_modules/which/bin/node_modules:/proc/cygdrive/c/Users/danie/Documents/.tampermonkey-scripts/roll20/chrome-extension/node_modules/which/node_modules:/proc/cygdrive/c/Users/danie/Documents/.tampermonkey-scripts/roll20/chrome-extension/node_modules:/proc/cygdrive/c/Users/danie/Documents/.tampermonkey-scripts/roll20/node_modules:/proc/cygdrive/c/Users/danie/Documents/.tampermonkey-scripts/node_modules:/proc/cygdrive/c/Users/danie/Documents/node_modules:/proc/cygdrive/c/Users/danie/node_modules:/proc/cygdrive/c/Users/node_modules:/proc/cygdrive/c/node_modules:/proc/cygdrive/c/Users/danie/Documents/.tampermonkey-scripts/roll20/chrome-extension/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../which/bin/node-which" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../which/bin/node-which" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../which/bin/node-which" $args
  } else {
    & "node$exe"  "$basedir/../which/bin/node-which" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
